import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {useEffect, useState} from 'react';
import Axios from 'axios';
// import {useState, useEffect} from 'react';

function App() {

  const[nombre, setNombre] = useState("");
  const[edad, setEdad] = useState("");
  const[pais, setPais] = useState("");
  const[ocupacion, setOcupacion] = useState("");
  const[aniostrabajo, setAniosTrabajo] = useState("");

  const [empleadosList, setEmpleados] = useState([]);
  const [showTable, setShowTable] = useState(false);


  //FUNCION AGREGAR REGISTRO
  //Axios es una librería que permite hacer peticiones HTTP desde el navegador o Node.js
  //En este caso, se utiliza para enviar una solicitud POST al servidor Express que se ejecuta en el puerto 3001.
  //La solicitud incluye los datos del nuevo empleado que se van a agregar a la base de datos.
  //Cuando la solicitud se completa con éxito, se muestra una alerta indicando que el empleado se ha registrado correctamente.
  //Si ocurre un error durante la solicitud, se captura y se muestra en la consola.
  //El evento preventDefault() se utiliza para evitar que el formulario se envíe de forma predeterminada, lo que recargaría la página.
  //Esto permite que la función add maneje el envío del formulario de manera personalizada.
  //El evento onClick se utiliza para llamar a la función add cuando se hace clic en el botón de registro.
  //El evento onChange se utiliza para actualizar el estado de los campos del formulario a medida
  //que el usuario ingresa datos en ellos.
  //El estado se actualiza utilizando las funciones setNombre, setEdad, setPais,
  const add= (event) => {
    event.preventDefault();
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      ocupacion: ocupacion,
      anios: aniostrabajo

    }).then((response) => {

      // useEffect(() => {
      // App.getEmpleados();
      // }, []);

      alert("Empleado registrado correctamente");
      setNombre("");
      setEdad("");
      setPais("");
      setOcupacion("");
      setAniosTrabajo("");


    });
  };

  //FUNCION PARA TRAER Y LISTAR EMPLEADOS
  // const getEmpleados = () => {
  //   Axios.get("http://localhost:3001/empleados").then((response) => {
  //     console.log(response.data);

  //   });
  // };

  //FUNCION PARA LISTAR EMPLEADOS
  App.getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response) => {
      setEmpleados(response.data);
    });
  };


  // const mostrarDatos = (event) => {
  //   event.preventDefault();
  //   alert("Nombre:", nombre);
  // };


return (
    <div className="App py-5">
      <div className="card text-center">
        <div className="card-header">
          <h1 className="text-primary">CRUD Empleados</h1>
          <h2 className="text-secondary">Registro y Listado</h2>
        </div>

        <div className="card-body">
          <div className="container my-5 d-flex justify-content-center">
            <form onSubmit={add} className="row g-4 px-5 py-5 bg-secondary-emphasis rounded-3 shadow-lg col-12 col-md-8">
              <h3 className="w-100 text-center mb-4 fw-semibold">Registro de Empleado</h3>

              {/* Nombre */}
              <div className="row mb-3 col-md-12">
                <label htmlFor="nombre" className="col-sm-4 col-form-label text-end fw-semibold">Nombre completo:</label>
                <div className="col-sm-8">
                  <input
                    type="text"
                    id="nombre"
                    className="form-control form-control-lg border-primary is-valid"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                    required
                  />
                  <div className="valid-feedback">¡Correcto!</div>
                </div>
              </div>

              {/* Edad */}
              <div className="row mb-3 col-md-12">
                <label htmlFor="edad" className="col-sm-4 col-form-label text-end fw-semibold">Edad:</label>
                <div className="col-sm-8">
                  <input
                    type="number"
                    id="edad"
                    className="form-control form-control-lg border-primary is-valid"
                    value={edad}
                    onChange={(e) => setEdad(e.target.value)}
                    required
                  />
                  <div className="valid-feedback">¡Correcto!</div>
                </div>
              </div>

              {/* País */}
              <div className="row mb-3 col-md-12">
                <label htmlFor="pais" className="col-sm-4 col-form-label text-end fw-semibold">País:</label>
                <div className="col-sm-8">
                  <input
                    type="text"
                    id="pais"
                    className="form-control form-control-lg border-primary is-valid"
                    value={pais}
                    onChange={(e) => setPais(e.target.value)}
                    required
                  />
                  <div className="valid-feedback">¡Correcto!</div>
                </div>
              </div>

              {/* Ocupación */}
              <div className="row mb-3 col-md-12">
                <label htmlFor="ocupacion" className="col-sm-4 col-form-label text-end fw-semibold">Ocupación:</label>
                <div className="col-sm-8">
                  <input
                    type="text"
                    id="ocupacion"
                    className="form-control form-control-lg border-primary is-valid"
                    value={ocupacion}
                    onChange={(e) => setOcupacion(e.target.value)}
                    required
                  />
                  <div className="valid-feedback">¡Correcto!</div>
                </div>
              </div>

              {/* Años de trabajo */}
              <div className="row mb-3 col-md-12">
                <label htmlFor="aniostrabajo" className="col-sm-4 col-form-label text-end fw-semibold">Años de trabajo:</label>
                <div className="col-sm-8">
                  <input
                    type="number"
                    id="aniostrabajo"
                    className="form-control form-control-lg border-primary is-valid"
                    value={aniostrabajo}
                    onChange={(e) => setAniosTrabajo(e.target.value)}
                    required
                  />
                  <div className="valid-feedback">¡Correcto!</div>
                </div>
              </div>

              {/* Botón Registrar */}
              <div className="col-12 text-center">
                <button type="submit" className="btn btn-primary btn-lg px-4">Registrar</button>
              </div>
            </form>
          </div>
        </div>

        <div className="card-footer text-body-secondary">
          <div className="col-12 text-center mb-3">
            <button
              type="button"
              className="btn btn-secondary btn-lg px-4"
              onClick={getEmpleados}
            >
              Listar Empleados
            </button>
          </div>

          {/* Mostrar tabla solo al presionar Listar */}
          {showTable && (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Edad</th>
                    <th scope="col">País</th>
                    <th scope="col">Ocupación</th>
                    <th scope="col">Años Trabajados</th>
                  </tr>
                </thead>
                <tbody>
                  {empleadosList.map((val, key) => (
                    <tr key={key}>
                      <th scope="row">{key + 1}</th>
                      <td>{val.nombres}</td>
                      <td>{val.edad}</td>
                      <td>{val.pais}</td>
                      <td>{val.ocupacion}</td>
                      <td>{val.anios}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
