import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from 'react';
import Axios from 'axios';

function App() {

  const[nombre, setNombre] = useState("");
  const[edad, setEdad] = useState("");
  const[pais, setPais] = useState("");
  const[ocupacion, setOcupacion] = useState("");
  const[aniostrabajo, setAniosTrabajo] = useState("");

  const [empleadosList, setEmpleados] = useState([]);


  //FUNCION AGREGAR REGISTRO
  //Axios es una librería que permite hacer peticiones HTTP desde el navegador o Node.js
  //En este caso, se utiliza para enviar una solicitud POST al servidor Express que se ejecuta en el puerto 3001.
  //La solicitud incluye los datos del nuevo empleado que se van a agregar a la base de datos.
  //Cuando la solicitud se completa con éxito, se muestra una alerta indicando que el empleado se ha registrado correctamente.
  //Si ocurre un error durante la solicitud, se captura y se muestra en la consola.
  //El evento preventDefault() se utiliza para evitar que el formulario se envíe de forma predeterminada, lo que recargaría la página.
  //Esto permite que la función add maneje el envío del formulario de manera personalizada.
  //El evento onClick se utiliza para llamar a la función add cuando se hace clic en el botón de registro.
  //El evento onChange se utiliza para actualizar el estado de los campos del formulario a medida
  //que el usuario ingresa datos en ellos.
  //El estado se actualiza utilizando las funciones setNombre, setEdad, setPais,
  const add= (event) => {
    event.preventDefault();
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      ocupacion: ocupacion,
      anios: aniostrabajo

    }).then((response) => {
      alert("Empleado registrado correctamente");
      //getEmpleados();
    });
  };

  //FUNCION PARA TRAER Y LISTAR EMPLEADOS
  // const getEmpleados = () => {
  //   Axios.get("http://localhost:3001/empleados").then((response) => {
  //     console.log(response.data);

  //   });
  // };

  //FUNCION PARA LISTAR EMPLEADOS
  App.getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response) => {
      setEmpleados(response.data);
    });
  };


  // const mostrarDatos = (event) => {
  //   event.preventDefault();
  //   alert("Nombre:", nombre);
  // };


  return (
    <div className="App py-5">
      <form className="row g-4 px-5 py-5 container mx-auto bg-text-secondary-emphasis rounded-3">
        <div className="row mb-3 col-sm-4">
          <label className="col-sm-8 form-label text-start">Nombre completo:</label>
          <input value={nombre}
            onChange={(event) => {
              setNombre(event.target.value);
            }}
          type="text" className="form-control is-valid" id="nombre" required />
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="row mb-3">
  <label htmlFor="aniostrabajo" className="col-sm-4 col-form-label text-end">
    Años de trabajo:
  </label>
  <div className="col-sm-8">
    <input
      type="number"
      className="form-control is-valid"
      id="aniostrabajo"
      value={aniostrabajo}
      onChange={(event) => {
        setAniosTrabajo(event.target.value);
      }}
      required
    />
    <div className="valid-feedback">Looks good!</div>
  </div>
</div>

        <div className="col-md-12">
          <label  className="form-label " >Edad:</label>
          <input value={edad}
              onChange={(event) => {
              setEdad(event.target.value);
            }}
          type="number" className="form-control is-valid" id="edad" required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-12">
          <label className="form-label">Pais:</label>
          <input value={pais}
              onChange={(event) => {
              setPais(event.target.value);
            }}
          type="text" className="form-control is-valid" id="pais"  required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-12">
          <label className="form-label">Ocupación:</label>
          <input value={ocupacion}
              onChange={(event) => {
              setOcupacion(event.target.value);
            }}
          type="text" className="form-control is-valid" id="ocupacion"  required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-12">
          <label className="form-label align">Años de trabajo:</label>
          <input value={aniostrabajo}
              onChange={(event) => {
              setAniosTrabajo(event.target.value);
            }}
          type="number" className="form-control is-valid" id="aniostrabajo" required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-12">
          <button onClick={add} className='btn btn-primary' type="button">Registrar</button>
        </div>

        {
        empleadosList.map((val, key) => {
          return <div className=''>{val.nombre}</div>
        })}


      </form>

    </div>
  );
}

export default App;
