import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from 'react';
import Axios from 'axios';

function App() {
  const[nombre, setNombre] = useState("");
  const[edad, setEdad] = useState("");
  const[pais, setPais] = useState("");
  const[ocupacion, setOcupacion] = useState("");
  const[aniostrabajo, setAniosTrabajo] = useState("");


  //evento para boton
  const add= (event) => {
    Axios.post("http://localhost:3001/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      ocupacion: ocupacion,
      anios: aniostrabajo
    }).then((response) => {
      alert("Empleado registrado correctamente");
    }).catch((error) => {
      console.error("There was an error!", error);
    });
  };





  const mostrarDatos = (event) => {
    event.preventDefault();
    alert("Nombre:", nombre);
  };
  return (
    <div className="App py-5">
      <form class="row g-4 px-5 py-5 container mx-auto bg-text-secondary-emphasis rounded-3">
        <div class="col-md-6">
          <label for="validationServer01" className="form-label">Nombre completo:</label>
          <input
            onChange={(event) => {
              setNombre(event.target.value);
            }}
          type="text" className="form-control is-valid" id="nombre" required />
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-6">
          <label for="validationServer02" className="form-label">Edad:</label>
          <input 
              onChange={(event) => {
              setEdad(event.target.value);
            }}
          type="number" className="form-control is-valid" id="edad" required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-6">
          <label for="validationServer03" className="form-label">Pais:</label>
          <input
              onChange={(event) => {
              setPais(event.target.value);
            }}
          type="text" className="form-control is-valid" id="pais"  required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-6">
          <label for="validationServer04" className="form-label">Ocupación:</label>
          <input
              onChange={(event) => {
              setOcupacion(event.target.value);
            }}
          type="text" className="form-control is-valid" id="ocupacion"  required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>

        <div className="col-md-6">
          <label for="validationServer05" className="form-label">Años de trabajo:</label>
          <input
              onChange={(event) => {
              setAniosTrabajo(event.target.value);
            }}
          type="number" className="form-control is-valid" id="aniostrabajo" required></input>
          <div className="valid-feedback">
            Looks good!
          </div>
        </div>


        <div class="col-12">
          <button onClick={mostrarDatos} type="submit">Registrar</button>
        </div>
      </form>

    </div>
  );
}

export default App;
